    ready() {
      let coordinatates = [];
      [].slice
        .call(this.$els.svg.querySelectorAll('path'))
        .forEach(country => {
          const { top, left, width, height } = country.getBoundingClientRect();
          coordinatates[country.classList[1]] = {
            x: left + width / 2,
            y: top + height / 2
          };
        });

      this.$dispatch('getCountriesCoordinates', coordinatates);
    },

    methods: {
      clickCountry: (name) => {
        console.log(name);
      }
    },

    events: {

